`update_time` int(11)  unsigned not null default 0  COMMENT '更新时间',
`delete_time` int(11) UNSIGNED DEFAULT NULL,
primary key(`id`),
unique key categoryname(`categoryname`)
)engine=innodb AUTO_INCREMENT=1 default charset=utf8;
